select.where:

           and students_id = #{studentsId}
           and submit_flag = #{submitFlag}
           and create_date = #{createDate}
           and update_date = #{updateDate}
           and homework_id = #{homeworkId}
           and file_id = #{fileId}
           and file_name like concat('%', #{fileName}, '%')
          and del_flag="0"

mapper.update:
update school_homework_detail
students_id = #{studentsId}, submit_flag = #{submitFlag}, create_date = #{createDate}, update_date = #{updateDate}, del_flag = #{delFlag}, homework_id = #{homeworkId}, file_id = #{fileId}, file_name = #{fileName}, pass_flag=#{passFlag},
where id = #{id}

mapper.select:
select ss.id, ss.students_id as studentsId, ss.students_name as studentsName, ss.academic_level as academicLevel, ss.father_name, ss.father_phone, ss.mother_name, ss.mother_phone, ss.home_address, ss._remarks, ss.ages, ss.admission_time as admissionTime, ss._state, ss.del_flag, ss.deptAlias, ss.userAlias , ss.birth, (select school_name from school_belong where id = ss.sbid) as 'sbname', (select `name` from school_specialty where id = ss.ssid) as 'ssname', ifnull(sc.nameclass,"") as nameclass , ifnull(ss.grade_id,"") as gradeId , ifnull(ss.class_id,"") as classId , shd.submit_flag, shd.file_id, shd.file_name, shd.id as did, (select file from school_homework_detail_detail where detailid=shd.id and studentid = ss.id and submittime like concat ('%',DATE_FORMAT(NOW(),'%Y-%m-%d') , '%')) as file, (select ontest from school_homework_detail_detail where detailid=shd.id and studentid = ss.id and submittime like concat ('%',DATE_FORMAT(NOW(),'%Y-%m-%d') , '%')) as ontest from schoolstudentslist ss left join school_class sc on sc.id=ss.class_id left join school_homework_detail shd on shd.students_id= ss.id
ss.del_flag='0' and sc.delete_flag='0'
and nameclass like #{nameclass}
and students_name like #{studentsName}
and shd.homework_id=#{homeworkId}
and grade_id=#{gradeId}
and class_id=#{classId}
