mapper.select:
select ss.id, ss.students_id as studentsId, ss.students_name as studentsName, ss.academic_level as academicLevel, ss.father_name, ss.father_phone, ss.mother_name, ss.mother_phone, ss.home_address, ss._remarks, ss.ages, ss.admission_time as admissionTime, ss._state, ss.del_flag, ss.deptAlias, ss.userAlias , ifnull(sc.nameclass,"") as nameclass , ifnull(ss.grade_id,"") as gradeId , ifnull(ss.class_id,"") as classId , srd.result, srd.absent_flag, srd.id as did from schoolstudentslist ss left join school_class sc on sc.id=ss.class_id left join school_result_detail srd on srd.students_id= ss.id
ss.del_flag='0' and sc.delete_flag='0'
and nameclass like #{nameclass}
and students_name like #{studentsName}
and srd.result_id=#{resultId}
and grade_id=#{gradeId}
and class_id=#{classId}

select r.curriculum_name as 'curriculm', r.exam_name as 'exam', r.exam_date as 'examdata', d.result as 'result', d.absent_flag as 'absentFlag' from school_result_detail d left join schoolstudentslist s on s.id=d.students_id left join school_result r on r.id=d.result_id where d.del_flag=0 and s.del_flag=0 and r.del_flag=0
and s.id=#{studentid}
and r.exam_date=#{examdata}
and r.curriculum_name like concat('%', #{curriculm}, '%')

mapper.update:
update school_result_detail
students_id = #{studentsId}, result = #{result}, absent_flag = #{absentFlag}, create_date = #{createDate}, update_date = #{updateDate}, del_flag = #{delFlag}, result_id = #{resultId},
where id = #{id}
